import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: MyHomePage(title: 'Flutter Demo Home Page'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  MyHomePage({Key? key, required this.title}) : super(key: key);
  final String title;
  @override
  _MyHomePageState createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> {
  int index = 0;
  List<Map> ourMemes = [
    {
      "text": "aaaaa",
      "img":
          "data:image/jpeg;base64,"
    },
    {
      "img":
          "https://th.bing.com/th/id/OIP.a0oQNm7-L7uN3KqIk_cZegHaG0?pid=ImgDet&rs=1"
    },
    {
      "img":
          "https://th.bing.com/th/id/OIP.ynot18Ib93OY3dVe5khPfwHaFL?pid=ImgDet&rs=1"
    }
  ];
  void _nextMeme() {
    setState(() {
      index++;
      if (index > ourMemes.length - 1) {
        index = 0;
      }
    });
  }

  void _lastMeme() {
    setState(() {
      index--;
      if (index < 0) {
        index = ourMemes.length - 1;
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            Container(
              child: Stack(
                children: <Widget>[
                  Image.network(ourMemes[index]["img"],
                      width: 300, height: 200, fit: BoxFit.fill),
                  Positioned(
                    top: 65.0,
                    child: ElevatedButton(
                      style: ButtonStyle(
                        backgroundColor: MaterialStateProperty.all(
                            Colors.blue.shade200.withOpacity(0.1)),
                      ),
                      child: Icon(Icons.arrow_back),
                      onPressed: _lastMeme,
                    ),
                  ),
                  Positioned(
                    top: 65.0,
                    right: 0,
                    child: ElevatedButton(
                      style: ButtonStyle(
                        backgroundColor: MaterialStateProperty.all(
                            Colors.blue.shade200.withOpacity(0.1)),
                      ),
                      child: Icon(Icons.arrow_forward),
                      onPressed: _nextMeme,
                    ),
                  ),
                  Indicator(index)
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}

class Indicator extends StatelessWidget {
  Indicator(this.myData);
  final int myData;
  @override
  Widget build(BuildContext context) {
    return Text(" תמונה מס. ${myData + 1}");
  }
}
